record.properties:
Height = 419
Width = 662
DoCreate = .T.
Caption = "Modificar Orden de Servicio"
KeyPreview = .T.
dfechacita = {}
dfechacre = {}
dfechafin = {}
dfechacierre = {}
nmodo = 0
Name = "Formamsl1"
Marco.Name = "Marco"

record.methods:
PROCEDURE Click
* Copia la informacion al ClipBoard
With Thisform
					
	* Datos de la primera pesta침a
	With .Pags.Page1
		
		cPrioridad = ''
		* Almacenar la prioridad
		Do Case
			Case .Prioridad.Value == 1
				cPrioridad = 'Baja'
			
			Case .Prioridad.Value == 2
				cPrioridad = 'Normal'
			
			Case .Prioridad.Value == 3
				cPrioridad = 'Urgente'
		EndCase
		
		m.DatosOrden = ;
				'ORDEN DE SERVICIO'+Chr(13)+;
				'===================='+Chr(13)+Chr(13)+;
				'Folio:'+Allt(.NumOrden.Value)+Chr(13)+;
				Allt(MexDate2(ThisForm.Pags.Page2.FechaCreacion.Value))+' '+Allt(ThisForm.Pags.Page2.HoraCreacion.Value)+Chr(13)+;
				'Status:'+ iif(Empty(ThisForm.Pags.Page2.FechaFin.Value), 'PENDIENTE', 'RESUELTA')+Chr(13)+Chr(13)+;
				'Contacto:'+Chr(13)+;
				'----------------------------'+Chr(13)+;
				Allt(.InfoCont.LeeContacto.Nombre.Value)+' ('+Proper(Allt(.InfoCont.Empresa.Value))+')'+Chr(13)+;
				Allt(.InfoCont.Telefono.Value)+Chr(13)+;
				Allt(.InfoCont.Mail.Value)+Chr(13)+Chr(13)+;				
				'Problema a Resolver:'+Chr(13)+;
				'------------------------------'+Chr(13)+;
				Allt(.Problema.Value)+Chr(13)+Chr(13)+;
				iif( empty(.EquipoRec.Value) , '' , ;
					'Equipo Recibido'+Chr(13)+;
					'------------------------------'+Chr(13)+;
					Allt(.EquipoRec.Value)+Chr(13)+Chr(13) )+;
				iif( empty(.Causas.Value), '',;
					'Causas:'+Chr(13)+;
					'------------------------------'+Chr(13)+;
					Allt(.Causas.Value)+Chr(13))+;
				Chr(13)
				
	EndWith
	
	* Datos de la segunda pesta침a	
	With .Pags.Page2
		
		cCalif = ''
		* Almacenar la calificacion
		Do Case
			Case .Calif.Value == 1
				cCalif = 'Pesimo'
				
			Case .Calif.Value == 2
				cCalif = 'Malo'
			
			Case .Calif.Value == 3
				cCalif = 'Regular'
			
			Case .Calif.Value == 4
				cCalif = 'Bueno'
			
			Case .Calif.Value == 5
				cCalif = 'Excelente'
				
		EndCase
		If .f.
			m.DatosOrden = m.DatosOrden+'Creada: '+MexDate2(.FechaCreacion.Value)+' '+Allt(.HoraCreacion.Value)+Chr(13)+;
					'Prioridad: '+cPrioridad+Chr(13)+;
					'Terminada: '+MexDate2(.FechaFin.Value)+' '+Allt(.HoraFin.Value)+Chr(13)+;
					'Avance: '+Allt(Str(.Avance.Value))+Chr(13)+;
					'Cerrada: '+MexDate2(.FechaCierre.Value)+' '+Allt(.HoraCierre.Value)+Chr(13)+;
					'Calificaci칩n: '+cCalif+Chr(13)+;
					'Pendiente por: '+Allt(.PendPor.Value)+Chr(13)+;
					'Factura: '+Allt(.Factura.Value)+Chr(13)+;
					'Importe: '+Allt(Str(.Importe.Value,8,2))+Chr(13)+;
					'Horas Invertidas: '+Allt(Str(.HInvertidas.Value,6,2))+Chr(13)+;
					'Servicios Realizados: '+Allt(Str(.Servicios.Value))+Chr(13)+;
					'Veces Reabierta: '+Allt(Str(.Veces.Value))+Chr(13)+;
					'Horas Estimadas: '+Allt(Str(.HEstimadas.Value,6,2))+Chr(13)+;
					'Importe Estimado: '+Allt(Str(.IngEstim.Value,8,2))+Chr(13)
		Endif
		
		m.DatosOrden = m.DatosOrden + ;		
				'SERVICIOS REALIZADOS'+Chr(13)+;
				'==========================='+Chr(13)+;
				ThisForm.Pags.Page2.Historial.Value
								
	EndWith
	
	_ClipText = m.DatosOrden	
	
EndWith

ENDPROC
